# -*- coding: utf-8 -*- 
import requests
import urlparse
from bs4 import BeautifulSoup

##取得頁數
file = open('tainan_culture_name.txt','w')
file.write("景點名稱"+"\n")
tainan_culture_link='http://tour.tainan.gov.tw/view.aspx?subject=culture'
tainan_culture = requests.get(tainan_culture_link) 
tainan_culture_text = tainan_culture.text.encode('utf-8')
tainan_culture_soup=BeautifulSoup(tainan_culture_text)
tainan_culture_page_number = tainan_culture_soup.find('span',{"id":"View1_SpotList1_lblPageCount"}).text.encode('utf-8')

##每頁取資料
for page in range(1,int(tainan_culture_page_number)+1):
    form_data_number='第'+str(page)+'頁'

    payload = { '__EVENTTARGET':'View1:SpotList1:ddlPageList'
      ,'__EVENTARGUMENT':''
      ,'__VIEWSTATE':''
      ,'View1:SpotList1:SpotQuery1:ddlSubject':'culture'
      ,'View1:SpotList1:SpotQuery1:ddlTown':''
      ,'View1:SpotList1:ddlPageList':form_data_number}
	  
    tainan_culture = requests.post(tainan_culture_link, data=payload) 
    tainan_culture_text = tainan_culture.text.encode('utf-8')
    tainan_culture_soup=BeautifulSoup(tainan_culture_text)
    tainan_culture_table=tainan_culture_soup.find('table',{"id":"View1_SpotList1_dtlList"})
    tainan_culture_spans = tainan_culture_table.findAll('span',{"id":"main-wrapper"})
	##另開連結取得景點名稱
    for tainan_culture_span in tainan_culture_spans:
        culture_alink = tainan_culture_span.find('a',{"href":True})['href']
        culture_link=urlparse.urljoin(tainan_culture_link,culture_alink)
        culture_page=requests.get(culture_link)
        culture_page_text = culture_page.text.encode('utf-8')
        culture_page_soup=BeautifulSoup(culture_page_text)
        culture_name = culture_page_soup.find('span',{"id":"View1_SpotDetail1_lblName"}).text.encode('utf-8')
        file.write(culture_name.strip()+"\n")
file.close()